import styles from "./styles.module.css";
import React, { useState, useEffect, useRef } from "react";

function DriverBall() {
  //   const [numberDrivers, setnumberDrivers] = useState(1);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const ballRef = useRef(null);

  useEffect(() => {
    const x = Math.floor(Math.random() * window.innerWidth);
    const y = Math.floor(Math.random() * window.innerHeight);
    setPosition({ x, y });
  }, []);

  useEffect(() => {
    const ball = ballRef.current;
    const x = Math.floor(Math.random() * window.innerWidth);
    const y = Math.floor(Math.random() * window.innerHeight);
    const distance = Math.sqrt((x - position.x) ** 2 + (y - position.y) ** 2);
    const duration = distance / 50; // adjust the speed of the animation
    ball.style.transition = `transform ${duration}s ease-out`;
    ball.style.transform = `translate(${x - position.x}px, ${
      y - position.y
    }px)`;
    setPosition({ x, y });
  }, [position]);

  return (
    <div>
      <div className={styles.DriverBall} ref={ballRef}></div>
    </div>
  );
}
export default DriverBall;
