import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";
import DriverBall from "../objects/DriverBall";

function Driver({ location }) {
  const [state, setState] = useState("SEARCHING");
  const [driverLocation, setDriverLocation] = useState(location);
  const [destination, setDestination] = useState(null);
  const [waitingTime, setWaitingTime] = useState(0);
  const [speed, setSpeed] = useState(1);

  const [passenger, setPassenger] = useState(null);

  // const [earnings, setEarnings] = useState(0);
  // const [rating, setRating] = useState(0);
  // const [raining, setRaining] = useState(false);
  // const [traffic, setTraffic] = useState(false);
  // const [emotion, setEmotion] = useState("angry");
  function setSpawnLocation() {
    const x = Math.random();
    const y = Math.random();
    setDriverLocation();
  }
  function search(passenger) {
    setPassenger(passenger);
    if (passenger) {
      setState("PICKUP");
    } else {
      setDriverLocation((prev) => prev + speed);
    }
  }

  function pickUp(passenger) {
    setDestination(passenger.location);
    if (driverLocation !== destination) {
      setDriverLocation((prev) => prev + speed);
      console.log(`driver current location when picking up: ${driverLocation}`);
    }
    if (driverLocation === destination) {
      setState("TRANSIT");
    }
  }

  function transit() {
    setDestination(passenger.destination);
    if (driverLocation !== destination) {
      setDriverLocation((prev) => prev + speed);
      console.log(`driver current location when transit: ${driverLocation}`);
    } else {
      setState("COMPLETED");
    }
  }

  function completed() {
    setState("SEARCHING");
  }

  return (
    <div>
      {/* Display driver state and location */}
      <p>Driver state: {state}</p>
      <p>Driver location: {driverLocation}</p>

      {/* Display passenger information if there is a passenger */}
      {passenger && (
        <div>
          <p>Passenger location: {passenger.location}</p>
          <p>Passenger destination: {passenger.destination}</p>
        </div>
      )}

      {/* Button to simulate picking up a passenger */}
      {state === "SEARCHING" && (
        <button onClick={() => search({ location: 10, destination: 20 })}>
          Pick up passenger
        </button>
      )}

      {/* Button to simulate transit to passenger's destination */}
      {state === "PICKUP" && (
        <button onClick={() => pickUp(passenger)}>Start transit</button>
      )}

      {/* Button to simulate completing the ride */}
      {state === "TRANSIT" && <button onClick={transit}>Complete ride</button>}

      {/* Button to reset driver state and location */}
      {state === "COMPLETED" && <button onClick={completed}>Reset</button>}
    </div>
  );
}

export default Driver;
