import React, { useState, useEffect } from "react";

function Driver({ loc }) {
  const [driverLocation, setDriverLocation] = useState({ x: loc.x, y: loc.y });
  const [state, setState] = useState("SEARCHING");
  const [passenger, setPassenger] = useState(null);
  const circleStyle = {
    top: `${driverLocation.y}px`,
    left: `${driverLocation.x}px`,
  };

  function search(passengers) {
    setPassenger(passenger);
    if (passenger) {
      setState("PICKUP");
