import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

function Driver({ location: initialLocation, destination }) {
  const [location, setLocation] = useState(initialLocation);
  const [state, setState] = useState("SEARCHING");

  useEffect(() => {
    const speed = 0.1; // adjust as needed
    const distance = Math.abs(destination - initialLocation);
    const duration = distance / speed;

    const timer = setInterval(() => {
      if (location === destination) {
        clearInterval(timer);
        setState("COMPLETED");
      } else if (location < destination) {
        setLocation((prev) => prev + speed);
      } else {
        setLocation((prev) => prev - speed);
      }
    }, duration);

    return () => clearInterval(timer);
  }, [initialLocation, destination]);

  return (
    <div>
      <p>Driver state: {state}</p>
      <p>Driver location: {location}</p>
    </div>
  );
}
