import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

const Passenger = ({
  location,
  destination,
  happyfactor,
  driver,
  onPickup,
  onCancel,
}) => {
  const [color, setColor] = useState("blue");

  useEffect(() => {
    const timer = setTimeout(() => {
      if (!driver) {
        setColor("red");
        setTimeout(onCancel, 10000);
      }
    }, 10000);

    return () => clearTimeout(timer);
  }, [driver, onCancel]);

  const handleClick = () => {
    if (driver) {
      onPickup();
    }
  };

  return (
    <div
      className={styles.passenger}
      style={{
        backgroundColor: color,
        left: location.x,
        top: location.y,
      }}
      onClick={handleClick}
    />
  );
};

export default Passenger;
