import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

function Driver({ loc, dest }) {
  const [location, setLocation] = useState({ x: loc.x, y: loc.y });
  const [state, setState] = useState("SEARCHING");
  const [passenger, setPassenger] = useState(null);
  const [happydistance, setHappyDistance] = useState(10);
  const [happydestination, setHappyDestination] = useState(30);
  const [destination, setDestination] = useState({ x: dest.x, y: dest.y });
  const [speed, setSpeed] = useState(1);
  const [tolerance, setWaitingTolerance] = useState(5);

  const circleStyle = {
    top: `${location.y}px`,
    left: `${location.x}px`,
  };

  function search(passengers) {
    // insert timer
    const startTime = Date.now();
    const timer = setInterval(() => {
      const timeElapsed = Date.now() - startTime;

      if (timeElapsed == tolerance) {
        setState("EXIT");
      } else {
        if (
          passenger.location - location < happydistance &&
          passenger.destination - passenger.destination
        ) {
          setPassenger(passenger);
          setState("PICKING UP");
        } else {
          setLocation((prev) => prev + speed);
        }
      }
    }, 1000);
  }

  return <div className={styles.circle} style={circleStyle}></div>;
}

export default Driver;
