import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

const Driver = () => {
  const [location, setLocation] = useState({
    x: Math.floor(Math.random() * 500),
    y: Math.floor(Math.random() * 500),
  });
  const [destination, setDestination] = useState({
    x: Math.floor(Math.random() * 500),
    y: Math.floor(Math.random() * 500),
  });
  const [passenger, setPassenger] = useState(null);
  const [happyFactor, setHappyFactor] = useState(0);

  const search = () => {
    let timer;
    if (!passenger) {
      timer = setInterval(() => {
        const newDestination = {
          x: Math.floor(Math.random() * 500),
          y: Math.floor(Math.random() * 500),
        };
        setDestination(newDestination);
        if (
          location.x === newDestination.x &&
          location.y === newDestination.y
        ) {
          clearInterval(timer);
          search();
        }
      }, 5000);
    } else {
      const foundPassenger = passenger.find((p) => p.happyFactor < happyFactor);
      if (foundPassenger) {
        setDestination(foundPassenger.destination);
      } else {
        timer = setInterval(() => {
          const newDestination = {
            x: Math.floor(Math.random() * 500),
            y: Math.floor(Math.random() * 500),
          };
          setDestination(newDestination);
          if (
            location.x === newDestination.x &&
            location.y === newDestination.y
          ) {
            clearInterval(timer);
            search();
          }
        }, 5000);
      }
    }

    setTimeout(() => {
      clearInterval(timer);
      // check if there's still no passenger or no passenger that meets the happyFactor criteria after 10 seconds
      if (!passenger || !passenger.find((p) => p.happyFactor < happyFactor)) {
        // driver disappears
      }
    }, 10000);
  };

  const pickup = (passenger) => {
    setPassenger(passenger);
  };

  // const deliver = () => {
  //   setLocation(passenger.destination);
  //   // passenger and driver now become a unit
  //   // move together on the screen to the passenger's destination
  //   // driver disappears after reaching the destination
  //   // passenger disappears
  //   // driver goes back to function searching
  // };

  const completed = () => {
    search();
  };

  useEffect(() => {
    search();
  }, []);

  return (
    <div
      className={styles.driver}
      style={{ top: location.y, left: location.x }}
    >
      {/* green circle */}
    </div>
  );
};

export default Driver;
