import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

function PassengerBall({ container }) {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    setSpawnLocation();
  }, []);

  function setSpawnLocation() {
    const x = Math.floor(Math.random() * (container.current.offsetWidth - 40));
    const y = Math.floor(Math.random() * (container.current.offsetHeight - 40));
    setPosition({ x, y });
  }

  return (
    <div className={styles.ballContainer}>
      <div
        className={styles.PassengerBall}
        style={{ left: position.x, top: position.y }}
      ></div>
    </div>
  );
}

export default PassengerBall;
