import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";

function Passenger(location, destination) {
  const [state, setState] = useState("waiting");
  const [currentLocation, setCurrentLocation] = useState(location);
  const [waitingTime, setWaitingTime] = useState(0);
  const [driver, setDriver] = useState(null);

  useEffect(() => {
    const appearTime = (Date.now() / 1000) | 0;
    console.log(`Passenger appear at ${appearTime}`);

    function carArrived(timestamp) {
      if (driver.currentLocation === location) {
        const waitingTime = timestamp - appearTime;
        console.log(`Passenger waiting time: ${waitingTime}`);
        setWaitingTime(waitingTime);
        setState("transit");
      }
    }

    function transit() {
      setCurrentLocation(driver.currentLocation);
      console.log(
        `passenger current location when transit: ${currentLocation}`
      );
      if (currentLocation === destination) {
        setState("arrived");
      }
    }

    let intervalId = null;
    if (state === "waiting") {
      intervalId = setInterval(() => {
        carArrived((Date.now() / 1000) | 0);
      }, 1000);
    } else if (state === "transit") {
      intervalId = setInterval(() => {
        transit();
      }, 1000);
    }

    return () => clearInterval(intervalId);
  }, [state, location, destination, driver]);

  useEffect(() => {
    if (state === "arrived") {
      console.log(`Total waiting time: ${waitingTime}`);
      // remove passenger from the screen
    }
  }, [state, waitingTime]);

  return (
    <div>
      <div>Passenger state: {state}</div>
      <div>Passenger current location: {currentLocation}</div>
    </div>
  );
}

export default Passenger;
