import styles from "./styles.module.css";
import React, { useState, useEffect, useRef } from "react";

function DriverBall({ container }) {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [target, setTarget] = useState({ x: 0, y: 0 });
  const ballRef = useRef(null);

  useEffect(() => {
    setRandomTarget();
  }, []);

  useEffect(() => {
    moveTo(target);
  }, [target]);

  function setRandomTarget() {
    const x = Math.floor(
      Math.random() *
        (container.current.offsetWidth - ballRef.current.offsetWidth)
    );
    const y = Math.floor(
      Math.random() *
        (container.current.offsetHeight - ballRef.current.offsetHeight)
    );
    setTarget({ x, y });
  }

  function moveTo(target) {
    const ball = ballRef.current;
    const distance = Math.sqrt(
      (target.x - position.x) ** 2 + (target.y - position.y) ** 2
    );
    const duration = distance / 50; // adjust the speed of the animation
    ball.style.transition = `transform ${duration}s ease-out`;
    ball.style.transform = `translate(${target.x - position.x}px, ${
      target.y - position.y
    }px)`;
    setTimeout(() => {
      setPosition(target);
      setRandomTarget();
    }, duration * 1000);
  }

  return (
    <div className={styles.ballContainer}>
      <div className={styles.DriverBall} ref={ballRef}></div>
    </div>
  );
}
export default DriverBall;
